const rimraf = require('rimraf');
const fs = require('fs');

exports.rimraf = function (_path) {
  return new Promise((resolve, reject) => {
    rimraf(_path, err => {
      if (!err) resolve(true);else reject(err);
    });
  });
};

exports.rimrafSync = rimraf.sync;

exports.readdir = function (_path) {
  return new Promise((resolve, reject) => {
    fs.readdir(_path, (err, files) => {
      if (!err) resolve(files);else reject(err);
    });
  });
};